package org.elsoft.platform.${application};
import javax.transaction.TransactionManager;
import java.util.ArrayList;
import org.elsoft.platform.datacontrol.ELResolver;
import java.util.List;

public class TransactionManagerBuilderImpl implements TransactionManagerBuilder{

    private ELResolver el;

    public TransactionManagerBuilderImpl( ELResolver el )
    {
        this.el = el;
    }

	public TransactionManager getTransactionManager() throws Exception{

	   	       List<Class<?>>  clazzes = (List<Class<?>>) el.getValue( "clazzes" );


 		       ArrayList<Class<?>> ls = new ArrayList<Class<?>>();
 		       if (clazzes != null)
 		           ls.addAll(clazzes);

               ls.add( org.elsoft.platform.persistence.ObjectsID.class);


		          ls.add(${ant.resPackageName}.${ant.resTypeName}.class);



        return (TransactionManager) new org.elsoft.platform.persistence.TransactionManagerImpl(ls);
	}

}
