private String field;
public void setField(String field){
	this.field = field;
}
public void fileLoader(org.primefaces.event.FileUploadEvent event){
	byte[] bytes = event.getFile().getContents();
	Object obj =  getMode().getControl().getCurrentObject();
	Reflection.call(obj, "set" + org.apache.commons.lang.StringUtils.capitalize(field), bytes);
}
