	public void returnResult( javax.faces.event.ActionEvent event )
	{
	Object val=null;




	val = Reflection.call( getMode().getControl( ).getCurrentObject( ),"get${option.srcExpression}" );
	getMode().getElResolver( ).setValue( "${option.targetExpression}",val );



	}

    public void search( javax.faces.event.ActionEvent event )
    {
        getMode().setMode( RootModeSwitch.QUERY_MODE );
    }

    public void query( javax.faces.event.ActionEvent event )
    {
        getMode().setMode( RootModeSwitch.INSERT_MODE );
    }
